import React, {useState} from 'react'
import QuizPage from './components/QuizPage'
import { quizQuestions } from './data';
import QuizOptions from './components/QuizOptions';
import { correctAnswers } from './data';

const App = () => {
    const [page, setPage] = useState(1);
    const [correctCount, setCorrectCount] = useState(0);
    console.log(correctCount)

    function handlePageChange({target}){
        setPage((prev) => prev + +target.value);
    }
    function handlePageChangeFromOptions(event){
        setPage((prev) => prev + 1)
        if (event.target.value === correctAnswers[page - 1]){
            setCorrectCount((prev) => prev + 1)
        }
    }
    const firstPage = page === 1;
    const lastPage = page === quizQuestions.length;

  return (
    <div>
      <header>
        <p>page: {page}</p>
        <button disabled={firstPage} value={-1} onClick={handlePageChange}>previous</button>
        <button disabled={lastPage} value={1} onClick={handlePageChange}>next</button>
      </header>  
        <br /><br />

      <QuizPage pageNumber={page} />
      <QuizOptions onClick={handlePageChangeFromOptions} answersNumber={page} />
    </div>
  )
}

export default App
