import React from 'react'
import { quizAnswers } from '../data';

const QuizOptions = (props) => {
  const isIt = (props.answerNumber - 1) === quizAnswers.length;

  return (
    <div>
      {quizAnswers[props.answersNumber - 1].map((answer) => {
        return <button onClick={props.onClick} key={answer}>{answer}</button>
      })}
    </div>
  )
}

export default QuizOptions
