import React, {useState} from 'react'
import QuizPage from './components/QuizPage'
import { quizQuestions } from './data';

const App = () => {
    const [page, setPage] = useState(1);

    function handlePageChange({target}){
        setPage((prev) => prev + +target.value);
    }
    const firstPage = page === 1;

  return (
    <div>
      <header>
        <p>page: {page}</p>
        <button disabled={firstPage} value={-1} onClick={handlePageChange}>previous</button>
        <button value={1} onClick={handlePageChange}>next</button>
      </header>  
        <br /><br />
      <QuizPage />
    </div>
  )
}

export default App
